// Extract SQL from agent response
const agentOutput = $input.first().json.output;

// Try to extract SQL from markdown code blocks
let sql = agentOutput;

const sqlBlockMatch = agentOutput.match(/```sql\n([\s\S]*?)```/i);
if (sqlBlockMatch) {
  sql = sqlBlockMatch[1].trim();
} else {
  const codeBlockMatch = agentOutput.match(/```([\s\S]*?)```/);
  if (codeBlockMatch) {
    sql = codeBlockMatch[1].trim();
  }
}

// Remove common prefixes
sql = sql.replace(/^(SQL:|Query:)\s*/i, '').trim();

return {
  json: {
    question:$('Webhook').first().json.body.chatInput,
    sql_query: sql,
    user_id:$('Webhook').first().json.body.sessionId,
    user_context:$('Webhook').first().json.body.chatInput
  }
};

// Parse evaluation result
const evalOutput = $input.item.json.output;

let evaluation;
try {
  // Try to extract JSON from markdown
  const jsonMatch = evalOutput.match(/```json\n([\s\S]*?)```/);
  if (jsonMatch) {
    evaluation = JSON.parse(jsonMatch[1]);
  } else if (evalOutput.match(/^\s*\{/)) {
    evaluation = JSON.parse(evalOutput);
  } else {
    throw new Error('No valid JSON found');
  }
} catch (e) {
  // Fallback: create default evaluation
  evaluation = {
    business_relevance: 7.0,
    data_accuracy: 7.0,
    actionability: 7.0,
    adoption_potential: 7.0,
    time_savings: 7.0,
    overall_score: 7.0,
    should_reject: false,
    feedback_reason: 'Evaluation parsing failed, using default scores',
    improvement_suggestions: 'N/A',
    business_impact: 'Query executed successfully',
    estimated_time_saved_minutes: 20
  };
}

return {
  json: {
    question: $('Extract SQL from Agent').item.json.question,
    sql_query: $('Extract SQL from Agent').item.json.sql_query,
    results: $('Execute SQL Query').all(),
    evaluation: evaluation,
    user_id: $('Extract SQL from Agent').item.json.user_id
  }
};

// Count iterations
const currentIteration = $('When chat message received').first().json.iteration || 0;

if (currentIteration >= 3) {
  // Max iterations reached, accept anyway
  return {
    json: {
      ...$input.item.json,
      evaluation: {
        ...$input.item.json.evaluation,
        should_reject: false,
        feedback_reason: 'Max iterations reached (3), accepting query'
      }
    }
  };
}

// Prepare for regeneration with feedback
const improvementPrompt = `Previous attempt failed. 

Original Question: ${$input.item.json.question}

Previous SQL: ${$input.item.json.sql_query}

Feedback: ${$input.item.json.evaluation.feedback_reason}

Suggestions: ${$input.item.json.evaluation.improvement_suggestions}

Please generate an IMPROVED SQL query that addresses these issues.`;

return {
  json: {
    chatInput: improvementPrompt,
    sessionId: $input.item.json.user_id,
    iteration: currentIteration + 1
  }
};

// Determine chart type based on data
const results = $('Parse Evaluation Result').item.json.results;
const question = $('Parse Evaluation Result').item.json.question.toLowerCase();

if (results.length < 2) {
  return {
    json: {
      should_visualize: false,
      reason: 'Not enough data points'
    }
  };
}

let chartType = 'bar'; // default

// Detect chart type from question
if (question.includes('trend') || question.includes('evoluci') || question.includes('ultimas') || question.includes('semanas')) {
  chartType = 'line';
} else if (question.includes('compar') || question.includes('top') || question.includes('ranking')) {
  chartType = 'bar';
} else if (question.includes('distribuci') || question.includes('proporci')) {
  chartType = 'pie';
}

// Detect columns
const firstRow = results[0];
const columns = Object.keys(firstRow);

const numericCols = columns.filter(col => typeof firstRow[col] === 'number');
const categoricalCols = columns.filter(col => typeof firstRow[col] === 'string');

const xColumn = categoricalCols[0] || columns[0];
const yColumn = numericCols[0] || columns[1];

return {
  json: {
    should_visualize: true,
    chart_type: chartType,
    x_column: xColumn,
    y_column: yColumn,
    data: results.slice(0, 20), // Limit to 20 points
    title: $('Parse Evaluation Result').item.json.question.substring(0, 60)
  }
};

// Compile final response
const evaluation = $('Parse Evaluation Result').item.json.evaluation;
const answer = $('Answer Generator Agent').item.json.output;
const vizData = $('Detect Visualization Needs').item.json;

// Obtener datos del grÃ¡fico si existe (nota: ahora es "Generate Plotly Chart1")
let chartData = null;
if (vizData.should_visualize) {
  try {
    // Intenta obtener datos del nodo del grÃ¡fico
    const chartNodes = $('Generate Plotly Chart1').all();
    if (chartNodes && chartNodes.length > 0) {
      chartData = chartNodes[0].json;
    }
  } catch(e) {
    console.log('No chart data available:', e.message);
  }
}

// Determinar emoji basado en el score
const emoji = evaluation.overall_score >= 8 ? 'ðŸŒŸ' : 
              evaluation.overall_score >= 6 ? 'âœ…' : 'âš ï¸';

// Construir respuesta final
const response = {
  success: true,
  question: $('Parse Evaluation Result').item.json.question,
  sql_query: $('Parse Evaluation Result').item.json.sql_query,
  results: $('Parse Evaluation Result').item.json.results,
  row_count: $('Parse Evaluation Result').item.json.results.length,
  answer: answer,
  business_value: {
    overall_score: evaluation.overall_score,
    criteria_scores: {
      business_relevance: evaluation.business_relevance,
      data_accuracy: evaluation.data_accuracy,
      actionability: evaluation.actionability,
      adoption_potential: evaluation.adoption_potential,
      time_savings: evaluation.time_savings
    },
    business_impact: evaluation.business_impact,
    time_saved_minutes: evaluation.estimated_time_saved_minutes,
    improvement_iterations: 0  // Se actualizarÃ¡ si implementas iteraciones
  },
  visualization: null,  // Inicializar como null
  formatted_response: ''  // Se construye al final
};

// Agregar visualizaciÃ³n si existe
if (vizData.should_visualize && chartData && chartData.success) {
  response.visualization = {
    success: true,
    chart_type: vizData.chart_type,
    chart_json: chartData.chart_json,
    chart_html: chartData.html,
    embed_html: chartData.embed_html,
    statistics: chartData.statistics,
    data_points: vizData.data.length
  };
}

// Construir respuesta formateada
let formattedResponse = `${emoji} **Rappi Analytics**\n\n`;
formattedResponse += `${answer}\n\n`;
formattedResponse += `ðŸ’¡ **Impacto:** ${evaluation.business_impact}\n`;
formattedResponse += `ðŸ“Š **Score:** ${evaluation.overall_score.toFixed(1)}/10\n`;
formattedResponse += `â±ï¸ **Tiempo ahorrado:** ~${evaluation.estimated_time_saved_minutes} min`;

if (response.visualization) {
  formattedResponse += `\nðŸ“ˆ **VisualizaciÃ³n:** GrÃ¡fico ${vizData.chart_type} con ${vizData.data.length} puntos de datos`;
}

response.formatted_response = formattedResponse;

// Para chat response, incluir versiÃ³n simplificada
response.chat_response = {
  text: formattedResponse,
  visualization_available: response.visualization !== null,
  score: evaluation.overall_score,
  success: true
};

return { json: response };

