const data = $json.data;
const chartType = $json.chart_type;
const title = $json.title;
const xColumn = $json.x_column;
const yColumn = $json.y_column;

// Preparar arrays para los ejes
const xValues = data.map(row => row[xColumn]);
const yValues = data.map(row => row[yColumn]);

// Construir la serie de datos para Plotly segÃºn el tipo de grÃ¡fico
let plotlyData;
if (chartType === 'line') {
  plotlyData = [{
    x: xValues,
    y: yValues,
    type: 'scatter',
    mode: 'lines+markers',
    marker: { color: '#FF6B35', size: 8 },
    line: { color: '#FF6B35', width: 2 }
  }];
} else if (chartType === 'bar') {
  plotlyData = [{
    x: xValues,
    y: yValues,
    type: 'bar',
    marker: {
      color: '#FF6B35',
      opacity: 0.8
    }
  }];
} else if (chartType === 'pie') {
  plotlyData = [{
    values: yValues,
    labels: xValues,
    type: 'pie',
    marker: {
      colors: [
        '#FF6B35', '#F7931E', '#FDB462', '#FB8072',
        '#80B1D3', '#8DD3C7', '#BEBADA', '#FFFFB3'
      ]
    }
  }];
}

// Layout para el grÃ¡fico
const layout = {
  title: {
    text: title,
    font: { size: 18, color: '#333' }
  },
  xaxis: {
    title: xColumn,
    gridcolor: '#E0E0E0'
  },
  yaxis: {
    title: yColumn,
    gridcolor: '#E0E0E0'
  },
  paper_bgcolor: '#FAFAFA',
  plot_bgcolor: 'white',
  margin: { t: 60, r: 30, l: 60, b: 60 },
  font: { family: 'Arial, sans-serif' },
  showlegend: (chartType === 'pie'),
  height: 400,
  width: 600
};

