// Config para embeber
const chartConfig = {
  data: plotlyData,
  layout: layout,
  config: {
    responsive: true,
    displayModeBar: true,
    displaylogo: false
  }
};

// CÃ¡lculo de estadÃ­sticas simples
const validY = yValues.filter(v => !isNaN(v));
const stats = {
  min: Math.min(...validY),
  max: Math.max(...validY),
  avg: validY.reduce((sum, v) => sum + v, 0) / validY.length,
  count: data.length
};

return {
  json: {
    success: true,
    chart_type: chartType,
    chart_json: JSON.stringify(chartConfig),
    chart_config: chartConfig,
    html: chartHtml,
    embed_html: `<div id="chart-${Date.now()}"></div>
      <script>
        Plotly.newPlot('chart-${Date.now()}', ${JSON.stringify(plotlyData)}, ${JSON.stringify(layout)});
      </script>`,
    statistics: stats,
    data_summary: {
      x_values: xValues.slice(0, 5),
